import React ,{useEffect, useState} from "react";
import './App.css';
import SocketIO from 'socket.io-client';

const socket = SocketIO("http://localhost:3001", {transports: ['websocket', 'polling', 'flashsocket']});


function App() {

    const [text,setText] = useState('');
    const [data,setData] = useState([]);

    useEffect(() => {
      socket.on('welcome',(data) =>{
          console.log(data);
          alert(data.text);
      });
    }, []);


    const sendData = () => {
        socket.emit('send_data', {
            site_name: 'https://webluxya.com'
        });
    };

    return (
      <div>
          <div>
                <input style={{padding:10, width:300,borderRadius:5, border: '1px solid #'}} type={text} onChange={(event) =>setText(event.target.value)}/>
          </div>
        <button onClick={sendData}>GÃ¶nder</button>
      </div>
    );
}

export default App;
